import React from 'react';
import { useNavigate } from 'react-router-dom';
import "./Chapter1EducationalPage.css";

export const Chapter1EducationalPage = (text) => {
  let navigate = useNavigate();

  const routeChange = () => {
    let path = '/chapter_2';
    navigate(path);
  };

  return (
    <div>
      <h1>{text}</h1>
      <p>Here are some things to look out for in Love Scams & Tips to keep you safe</p>
      <ol>
        <li>
            Stay Informed: Keep up to date with common scam tactics to help you better identify
            potential scams. 
        </li>
        <li>
            Look out for fake profiles: Pay attention to overly poetic phrases, 
            repetitive words and inconsistencies in their pictures and words. 
        </li>
        <li>
            Exercise Caution: Quick-moving relationships, especially those that 
            swiftly transition to less regulated communication platforms, should raise suspicions.
        </li>
        <li>
            Beware of financial requests: Regardless of any reason, request for monetary assistance
            are classic hallmarks of love scams. Never send money to someone you just met online.
        </li>
      </ol>
      <p>
        If caught in a love scam, sever all contact with the scammer, inform your banks if any 
        transactions have been made, and lastly file a police report. 
      </p>
      <div className="move-on">
        <button onClick={routeChange}>Chapter 2</button>
      </div>
    </div>
  );
}
